mapper.select:
SELECT file.file_code FROM sys_file_info file WHERE file.file_id IN
#{fileId}
GROUP BY file.file_code

mapper.update:
UPDATE sys_file_info file
SET file.del_flag = #{delFlag}
WHERE file.file_code IN

    #{fileCode}

UPDATE sys_file_info file
SET file.del_flag = #{delFlag}
WHERE file.file_id IN

    #{fileId}
